document.addEventListener("DOMContentLoaded", function () {
    // Function to load external HTML files
    function includeHTML(elementId, filePath) {
        fetch(filePath)
            .then(response => response.text())
            .then(data => document.getElementById(elementId).innerHTML = data)
            .catch(error => console.error(`Error loading ${filePath}:`, error));
    }

    // Load header and footer
    includeHTML("header", "components/header.html");
    includeHTML("footer", "components/footer.html");

    // Set HTML validation link
    let htmlValidator = document.getElementById("validation_link_html");
    if (htmlValidator) {
        htmlValidator.setAttribute("href", "https://validator.w3.org/check?uri=" + location.href);
    }

    // Set CSS validation link
    let cssValidator = document.getElementById("validation_link_css");
    if (cssValidator) {
        cssValidator.setAttribute("href", "https://jigsaw.w3.org/css-validator/validator?uri=" + location.href);
    }
});
